var ready=function(){$(".start-conversation").click(function(t){t.preventDefault();var e=$(this).data("sid"),n=$(this).data("rip");$.post("/conversations",{sender_id:e,recipient_id:n},function(t){chatBox.chatWith(t.conversation_id)})}),$(document).on("click",".toggleChatBox",function(t){t.preventDefault();var e=$(this).data("cid");chatBox.toggleChatBoxGrowth(e)}),$(document).on("click",".closeChat",function(t){t.preventDefault();var e=$(this).data("cid");chatBox.close(e)}),$(document).on("keydown",".chatboxtextarea",function(t){var e=$(this).data("cid");chatBox.checkInputKey(t,$(this),e)}),$("a.conversation").click(function(t){t.preventDefault();var e=$(this).data("cid");chatBox.chatWith(e)})};$(document).ready(ready),$(document).on("page:load",ready);